function DoClearAllScopesscope_list = findobj(get(0, 'children'), 'flat', 'tag', 'scope_window');	% all scopesfor j = 1:length(scope_list)	figure(scope_list(j));	axis_list = findobj(gcf, 'type', 'axes');	for i = 1:length(axis_list),		axes(axis_list(i)); cla;		set(gca, 'UserData', {});		line(get(gca, 'xlim'), [0 0], 'color', 'k', 'linestyle', ':');	% refresh zero lines		line([0 0], get(gca, 'ylim'), 'color', 'k', 'linestyle', ':');	endend