function align(page, pc)if nargin < 2	pc = [];endif nargin < 1	page = 0;endh0 = get(gcf, 'children');set(h0, 'xtick', [200:300:1400]);set(h0, 'xticklabel', [-600:300:600]);for i = 1:length(h0)	set(gcf, 'currentaxes', h0(i));	h = vlin(800, 'k');	if ~isempty(pc)		h(2) = vlin(800-pc, 'k');	end	if exist('page')		if page == 0			set(h, 'linewidth', 2);			set(h, 'color', [1 1 1]);				else			set(h, 'linewidth', 1);			set(h, 'color', [0 0 0]);				end	endend