% clear_scopes.m% function clear_scopes% callback function to clear current scope window% JBB 1/12/04function clear_scopesaxis_list = findobj(gcf, 'type', 'axes');for i = 1:length(axis_list),	axes(axis_list(i)); cla;	set(gca, 'UserData', {});	line(get(gca, 'xlim'), [0 0], 'color', 'k', 'linestyle', ':');	% refresh zero lines	line([0 0], get(gca, 'ylim'), 'color', 'k', 'linestyle', ':');end