function colplot(x, y, c, opts, page, yticks, min_width)width_multiplier = 2;if nargin < 5 | isempty(page)	page = 1;endif page == 0	pencolor = [1 1 1];	bkgcolor = [0 0 0];else	pencolor = [0 0 0];	bkgcolor = [1 1 1];endif nargin < 7 | isempty(min_width)	if page == 0		min_width = 2;	else		min_width = 1;	endendif nargin < 4 | isempty(opts)	opts = '';endset(gcf, 'color', bkgcolor);set(gcf, 'inverthardcopy', 'off');if nargin < 3	hold on	for i = 1:length(x)		h = plot(x(:,i), y{i});		set(h, 'linewidth', min_width .* width_multiplier);	endelseif nargin < 2 | isempty(c)	hold on	h = plot(x, y, [opts]);	set(h, 'linewidth', min_width .* width_multiplier);	set(h, 'markersize', 6 .* min_width);			for i = 1:length(h)		set(h(i), 'markerfacecolor', get(h(i), 'color'));	endelse	hold on	for i = 1:size(y,2)		h = plot(x, y(:,i), [c{i} opts]);		set(h, 'linewidth', min_width .* width_multiplier);		set(h, 'markerfacecolor', c{i}(1));		set(h, 'markersize', 6 .* min_width);			endendset(gca, 'linewidth', min_width);if nargin >= 6 & ~isempty(yticks)	set(gca, 'ylim', [yticks(1) yticks(end)]);	set(gca, 'ytick', yticks);else	xx = get(gca, 'ytick');	if length(xx) > 5		xx = round([xx(1):(xx(end) ./ (round(length(xx) ./ 2)-1)):xx(end)]);		set(gca, 'ytick', xx);	endendyticks = get(gca, 'ytick');set(gca, 'box', 'off');set(gca, 'tickdir', 'out');set(gca, 'ticklength', get(gca, 'ticklength') .* 1.5);set(gca, 'fontsize', 18 + (min_width-1) .* 6);set(gca, 'xcolor', pencolor);set(gca, 'ycolor', pencolor);set(gca, 'color', bkgcolor);