function n1 = degnorm(n1)%zz = find(n1 >= 200);%n1(zz) = n1(zz) - 360;%zz = find(n1 < -180);%n1(zz) = n1(zz) + 360;% disp(n1)% q1 = n1;% % s1 = sign(n1(1));% % for i = 2:length(n1)% 	if abs(n1(i) - n1(i-1)) > 90% 		if sign(n1(i)) == -1% 			n1(1:i-1) = n1(1:i-1) -360;% 		else% 			n1(1:i-1) = n1(1:i-1) + 360;% 		end% 	end% end% % % % if any(q1 ~= n1)% % 	keyboard% % end% % % % % % % %% % % % if any(n1(5:end) < -200)% % 	n1 = n1 + 360;% % end% % % % if any(n1(5:end) > 200)% %  	n1 = n1 - 360;% % end% % % % zz = find(n1 < -180);% n1(zz) = n1(zz) + 360;% % zz = find(n1 > 180);% n1(zz) = n1(zz) - 360;% % [xx ii] = max(abs(n1));% xx = xx .* sign(n1(ii));% % if abs(xx) > 180%% if max(n1) > 180% 	n1 = n1 - 360;% elseif min(n1) < -180% 	n1 = n1 + 360;% endif n1(1) > 180	n1 = n1 - 360;elseif n1(1) < -180	n1 = n1 + 360;endfor i = 2:length(n1)%	keyboard	while n1(i)-n1(i-1) <= -120		if n1(i-1) > 200			n1(1:i-1) = n1(1:i-1) - 360;		else				n1(i:end) = n1(i:end) + 360;		end	end		while n1(i)-n1(i-1) >= 120		if n1(i-1) < -200			n1(1:i-1) = n1(1:i-1) + 360;		else			n1(i:end) = n1(i:end) - 360;		end	endendwhile any(n1 > 240)	n1 = n1 - 360;endwhile any(n1 < -240)	n1 = n1 + 360;endif any(abs(diff(n1(2:end))) > 180)	n1(find(n1 > 180)) = n1(find(n1 > 180)) - 360;% 	n1(1:2) = n1(1:2) - 360;% 	keyboardend			