function yticks = den2b(sortvar, spkvar, correctvar, twin, sig, page, yticks, cols, min_width)% function yticks = den2b(sortvar, spkvar, correctvar, twin, sig, page,% yticks, col1, col2, col3, min_width)width_multiplier = 1;if nargin < 6 | isempty(page)	page = 1;endif page == 0	pencolor = [1 1 1];	bkgcolor = [0 0 0];else	pencolor = [0 0 0];	bkgcolor = [1 1 1];endif nargin < 9 | isempty(min_width)	if page == 0		min_width = 2;	else		min_width = 1;	endendif nargin < 8 | isempty(cols)	cols = {'r', 'g', 'c', 'y', 'm', 'w'};endif nargin < 5 | isempty(sig)	sig = 10;endif nargin < 4 | isempty(twin)	twin = [20:size(spk1,1)-20];else	twin = [twin(1):twin(2)];endset(gcf, 'color', bkgcolor);set(gcf, 'inverthardcopy', 'off');set(gca, 'linewidth', min_width);hold ondirs = unique(sortvar);for i = 1:length(dirs)    zz = find(correctvar == 1 & sortvar == dirs(i));    h = uplotb(spkvar(:,zz), cols{i}, sig, twin);    set(h, 'linewidth', min_width .* width_multiplier);endif nargin >= 7 & ~isempty(yticks)	set(gca, 'ylim', [yticks(1) yticks(end)]);	set(gca, 'ytick', yticks);else	xx = get(gca, 'ytick');	if length(xx) > 5 & xx(end)-xx(1) > 10		xx = round([xx(1):(xx(end) ./ (round(length(xx) ./ 2)-1)):xx(end)]);		if ~all(xx == 0)			set(gca, 'ytick', xx);		end	endendyticks = get(gca, 'ytick');set(gca, 'box', 'off');set(gca, 'tickdir', 'out');set(gca, 'ticklength', get(gca, 'ticklength') .* 1.5);%set(gca, 'fontsize', 12 + (min_width-1) .* 4);set(gca, 'fontsize', 12 + (min_width-1) .* 12);set(gca, 'xcolor', pencolor);set(gca, 'ycolor', pencolor);set(gca, 'color', bkgcolor);%set(gca, 'xlim', [1 length(twin)-1]);