function ca = file_to_cellarray(filename, columns, conv)%function ca = file_to_cellarray(filename, columns, conv)if isempty(filename)	[filename, pathname] = uigetfile('*', 'Select a txt dbase file');		endfilespec = fopen(filename, 'rt');if ~isempty(filespec)	count = 0;	done = 0;		while ~done		n1 = fscanf(filespec, conv{1}, 1);		if ~isempty(n1)			count = count + 1;			ca{count, 1} = n1;			for j = 2:columns				ca{count,j} = fscanf(filespec, conv{j}, 1);			end		else			done = 1;		end	end	fclose(filespec);end