function [p1, numeric_offset, labels, num_choices] = get_colorize_info(c1, gvals, gpars)% function [p1, color_offset, labels, num_choices] = get_colorize_info(c1, gvals, gpars)if nargin < 2	global gvalsendif nargin < 3	global gparsendc1_strlist = get(c1, 'string');c1_str = lower(c1_strlist(get(c1, 'value')));p1 = strmatch(c1_str, gpars.namelist, 'exact');numeric_offset = 0;if ~isempty(p1)	num_choices = gpars.length(p1);else	c1_str = char(c1_str);	if strcmp(lower(c1_str), 'none') == 0 & (strcmp(lower(c1_str(end-6:end)), '.active') == 1 | strcmp(lower(c1_str(end-7:end)), '.correct') == 1)		p1 = lower(c1_str);		if strcmp(lower(c1_str(end-7:end)), '.correct') == 1			numeric_offset = 2;			num_choices = 3;		else			numeric_offset = 1;			num_choices = 2;		end	else		num_choices = 1;	endend% Get labelslabels = [];if ~isempty(p1)	if ischar(p1)		labels = [0:num_choices-1];	else		if gpars.dependent(p1)			labels = [1:gpars.length(p1)];		else			labels = eval([gpars.valuelist{p1}]);		end	endend