function col_idx = get_trial_color(p1, trials, gvals, gpars)% function col_idx = get_trial_color(p1, trials, gvals, gpars)if nargin < 4	global gparsendif nargin < 3	global gvalsendcol_idx = 1;if ~isempty(gvals) & ~isempty(p1)	for tr = 1:length(trials)		i = trials(tr);		if ~isempty(gvals(i).trialnum)			if ischar(p1)				col_idx(tr) = eval(['gvals(i).' p1]);			else				col_idx(tr) = gvals(i).index(p1);			end		end	endend