function include_trial = include_trial_filter(vals, filter_string)include_trial = 0;if isempty(vals) | ~isfield(vals, 'resp')	return;endswitch(lower(filter_string))	case 'all', include_trial = 1;	case 'triggered', include_trial = vals.resp.triggered;	case 'passed', include_trial = vals.resp.passed;	case 'responded', include_trial = vals.resp.num_sacs > 0;	case 'correct', include_trial = (vals.resp.correct == 1);endif isempty(include_trial)	include_trial = 0;end