function yticks = rasden2(spk1, spk2, twin, sig, page, yticks, col1, col2, ras_row_spacing, col3, col4, min_width)% function yticks = rasden2(spk1, spk2, twin, sig, page, yticks, col1, col2, ras_row_spacing, col3, col4, min_width)width_multiplier = 1;if nargin < 5 | isempty(page)	page = 1;endif page == 0	pencolor = [1 1 1];	bkgcolor = [0 0 0];else	pencolor = [0 0 0];	bkgcolor = [1 1 1];endif nargin < 12 | isempty(min_width)	if page == 0		min_width = 2;	else		min_width = 1;	endendif nargin < 11 | isempty(col4)	col4 = pencolor;endif nargin < 10 | isempty(col3)	col3 = pencolor;endif nargin < 9 | isempty(ras_row_spacing)	ras_row_spacing = 3;endif nargin < 8 | isempty(col2)	col2 = [0 1 0];endif nargin < 7 | isempty(col1)	col1 = [1 0 0];endif nargin < 6	yticks = [];endif nargin < 4 | isempty(sig)	sig = 10;endif nargin > 1 & isempty(spk1) & ~isempty(spk2)	spk1 = spk2;	spk2 = [];endif nargin < 3 | isempty(twin)	twin = [20:size(spk1,1)-20];else	twin = [twin(1):twin(2)];endset(gcf, 'color', bkgcolor);set(gcf, 'inverthardcopy', 'off');subplot(2,1,2);yticks = den2c(spk1, spk2, twin, sig, page, yticks, col1, col2, min_width);subplot(2,1,1);ys = ras1(spk1(twin,:), col3);if nargin > 1 & ~isempty(spk2)	h = hlin(ys + ras_row_spacing ./ 2, pencolor);	set(h, 'linewidth', min_width);	ys = ras1(spk2(twin, :), col4, ys+ras_row_spacing);endset(gca, 'linewidth', min_width);xx = get(gca, 'xlim');axis([xx(1) xx(2) 0 ys]);raster_set1