function ticks = scat(x, y, cols, page, ticks, min_width, semx, semy)% function ticks = scat(x, y, cols, page, ticks, min_width, semx, semy)%width_multiplier = 2;if nargin < 4 | isempty(page)	page = 1;endif page == 0	pencolor = [1 1 1];	bkgcolor = [0 0 0];else	pencolor = [0 0 0];	bkgcolor = [1 1 1];endif nargin < 6 | isempty(min_width)	if page == 0		min_width = 2;	else		min_width = 1;	endendif nargin < 3 | isempty(cols)	if page == 0		cols = 'wo';	else		cols = 'ko';	endendclfset(gcf, 'color', bkgcolor);set(gcf, 'inverthardcopy', 'off');if nargin < 3 | ~iscell(cols)	h = plot(x, y, cols);	set(h, 'markersize', 4 .* min_width);			set(h, 'markerfacecolor', cols(1));else	hold on	for i = 1:length(x)		h = plot(x(i), y(i), cols{i});		set(h, 'markerfacecolor', cols{i}(1));		set(h, 'markersize', 4 .* min_width);			endendset(gca, 'linewidth', min_width);if nargin >= 5 & ~isempty(ticks)	set(gca, 'ylim', [ticks(1) ticks(end)]);	set(gca, 'xlim', [ticks(1) ticks(end)]);		set(gca, 'ytick', ticks);	set(gca, 'xtick', ticks);	else	xx = get(gca, 'ytick');	if length(xx) > 5		xx = round([xx(1):(xx(end) ./ (round(length(xx) ./ 2)-1)):xx(end)]);		set(gca, 'ytick', xx);		set(gca, 'xtick', xx);			endendticks = get(gca, 'ytick');set(gca, 'box', 'off');set(gca, 'tickdir', 'out');set(gca, 'ticklength', get(gca, 'ticklength') .* 1.5);set(gca, 'fontsize', 18 + (min_width-1) .* 6);set(gca, 'xcolor', pencolor);set(gca, 'ycolor', pencolor);set(gca, 'color', bkgcolor);h = line([ticks(1) ticks(end)], [ticks(1) ticks(end)]);set(h, 'linewidth', min_width, 'color', pencolor);set(gcf, 'paperposition', [1 1 5 .* 1.051664516129 5]);