function yticks = den2b(vals, twin, mult, page, yticks, cols, min_width, linesty)% function yticks = den2b(vals, twin, mult, page, yticks, cols, min_width, linesty)width_multiplier = 1;if nargin < 4 | isempty(page)	page = 0;endif page == 0	pencolor = [1 1 1];	bkgcolor = [0 0 0];else	pencolor = [0 0 0];	bkgcolor = [1 1 1];endif nargin < 8 | isempty(linesty)	linesty = ':';endif nargin < 7 | isempty(min_width)	if page == 0		min_width = 2;	else		min_width = 1;	endendif nargin < 6 | isempty(cols)		cols = {'r', 'g', 'c', 'y'};endif nargin < 3 | isempty(mult)	mult= 1;endif nargin < 2 | isempty(twin)	twin = [20:size(vals,1)-20];elseif length(twin) == 2	twin = [twin(1):twin(2)];endset(gcf, 'color', bkgcolor);set(gcf, 'inverthardcopy', 'off');set(gca, 'linewidth', min_width);hold onvals = double(vals);for i = 1:size(vals, 3)	% get rid of edge artifacts, if instructed	pvals = ones(size(vals,1), 3) .* nan;	pvals(twin,1) = mean(vals(twin,:,i)')';	temp = sem(vals(:,:,i)')';	if linesty == -1		if length(cols) == 1			ribbon2(pvals(:,1), temp .* 2, cols);		else			ribbon2(pvals(:,1), temp .* 2, cols{i});		end			else		pvals(twin,2) = pvals(twin,1) + temp(twin) .* mult;		pvals(twin,3) = pvals(twin,1) - temp(twin) .* mult;		if length(cols) == 1			h1 = plot(pvals(:,:), 'color', cols, 'linewidth', min_width .* width_multiplier);		else			h1 = plot(pvals(:,:), 'color', cols{i}, 'linewidth', min_width .* width_multiplier);		end		set(h1(2:3), 'linestyle', linesty);	endend	if nargin >= 6 & ~isempty(yticks)	set(gca, 'ylim', [yticks(1) yticks(end)]);	set(gca, 'ytick', yticks);else	xx = get(gca, 'ytick');	if length(xx) > 5 & xx(end)-xx(1) > 10		xx = round([xx(1):(xx(end) ./ (round(length(xx) ./ 2)-1)):xx(end)]);		set(gca, 'ytick', xx);	endendyticks = get(gca, 'ytick');set(gca, 'box', 'off');set(gca, 'tickdir', 'out');set(gca, 'ticklength', get(gca, 'ticklength') .* 1.5);%set(gca, 'fontsize', 24);set(gca, 'fontsize', 12 + (min_width-1) .* 12);set(gca, 'xcolor', pencolor);set(gca, 'ycolor', pencolor);set(gca, 'color', bkgcolor);set(gca, 'xlim', [1 length(twin)-1]);