function spikesg=sgauss(spikes, sfreq)% function spikesg=sgauss(spikes, sigma, sfreq)% GAUSS.M is a function to convolves a schall-type double exponential with spikes, outputs spikesg%  if nargin < 2	sfreq = 1000;endz1 = size(spikes, 1);z2 = size(spikes, 2);dist = [0:150]';rt = (1-exp(-dist/1)).*exp(-dist/20);sumrt = sum(rt);spt = spikes';spikesg = zeros(z2, z1);for i = 1:z2   temp = conv(rt, spt(i,:));   spikesg(i,:) = temp(1:z1) * 1000 / sumrt;end   spikesg = spikesg';