% stripext.m% function [file, ext] = stripext(filename)% strips extension from filename by removing all characters after first period (.)% JBB 10/17/00function [file, ext] = stripext(filename)if nargin < 1, help stripext; return; enddots = findstr(filename, '.');firstdot = min(dots);if ~isempty(dots),	file = filename(1 : firstdot-1);	ext = filename(firstdot : end);else	file = filename;	ext = [];end